Another solution is to use a trigger :
CREATE TABLE sample_table ( id TINYINT(4) NOT NULL AUTO_INCREMENT, xxx VARCHAR(255) NOT NULL DEFAULT 'value', date_insert TIMESTAMP NULL DEFAULT NULL, date_lastupdate TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (id) );
CREATE TRIGGER sample_table_on_insert BEFORE INSERT ON sample_table FOR EACH ROW SET NEW.date_insert = CURRENT_TIMESTAMP

